  `CLIPERXX` enum('','SEMANAL','MENSUAL','MANUAL') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA PERIODICIDAD DE LA FACTURACION',
  `CLIPERSE` enum('','LUNES','MARTES','MIERCOLES','JUEVES','VIERNES','SABADO','DOMINGO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA PERIODICIDAD DE LA FACTURACION',
  `CLIPERSH` time NOT NULL COMMENT 'HORA DE ENVIO',
  `CLIPERME` enum('','DIA FIJO','FECHA FIJA') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'BANDERA QUE INDICA COMO SE PARAMETRIZA LA FECHA MENSUAL',
  `CLIPERMS` enum('','LUNES','MARTES','MIERCOLES','JUEVES','VIERNES','SABADO','DOMINGO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA EL DIA DE LA FACTURACION CUANDO ES DIA DE LA SEMANA FIJO FACTURACION',
  `CLIPERMC` enum('','PRIMER','SEGUNDO','TERCERO','CUARTO','ULTIMO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA LOGICA DE QUE DIA DE LA SEMANA FIJO FACTURACION',
  `CLIPERMH` time NOT NULL COMMENT 'HORA DE ENVIO PARA DIA DE LA SEMANA FIJO',
  `CLIPERFE` int NOT NULL COMMENT 'indica el dia del mes en fecha fija',
  `CLIPERFH` time NOT NULL COMMENT 'HORA DE ENVIO PARA DIA DE LA fecha fija',
  `CLICONRI` enum('','COBRAR PRIMER DO','FACTURA CONSOLIDADA RIM','COBRAR EN DO DE RIM') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'bandera que indica la condicion para el cobro rim',
  `CLIPERRI` enum('','SEMANAL','MENSUAL','MANUAL') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA PERIODICIDAD DE LA FACTURACION RIM',
  `CLISEMRI` enum('','LUNES','MARTES','MIERCOLES','JUEVES','VIERNES','SABADO','DOMINGO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA PERIODICIDAD DE LA FACTURACION',
  `CLIHORRI` time NOT NULL COMMENT 'HORA DE ENVIO RIM',
  `CLIFERIM` enum('','DIA FIJO','FECHA FIJA') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'BANDERA QUE INDICA COMO SE PARAMETRIZA LA FECHA MENSUAL RIM',
  `CLIMSRIM` enum('','LUNES','MARTES','MIERCOLES','JUEVES','VIERNES','SABADO','DOMINGO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA EL DIA DE LA FACTURACION CUANDO ES DIA DE LA SEMANA FIJO FACTURACION RIM',
  `CLIDIRIM` enum('','PRIMER','SEGUNDO','TERCERO','CUARTO','ULTIMO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'INDICA LA LOGICA DE QUE DIA DE LA SEMANA FIJO FACTURACION RIM',
  `CLIHRERI` time NOT NULL COMMENT 'HORA DE ENVIO PARA DIA DE LA SEMANA FIJO RIM',
  `CLIHERIM` int NOT NULL COMMENT 'indica el dia del mes en fecha fija RIM',
  `CLIHFRIM` time NOT NULL COMMENT 'HORA DE ENVIO PARA DIA DE LA fecha fija RIM',
  `CLIRESTR` enum('','CLIENTE','SIACO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'responsable de pagos a tributos',
  `CLIFCUSD` enum('','NO','SI') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Indica si se factura en Dolares',
  `REGUSRXX` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Usuario Creacion del Registro',
  `REGFECXX` date NOT NULL COMMENT 'Fecha de Creacion del Registro',
  `REGHORXX` time NOT NULL COMMENT 'Hora de Creacion del Registro',
  `REGUSRMX` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Usuario de Modificacion',
  `REGFECMX` date NOT NULL COMMENT 'Fecha de Modificacion del Registro',
  `REGHORMX` time NOT NULL COMMENT 'Hora de Modificacion del Registro',
  `REGESTXX` enum('ACTIVO','INACTIVO') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Estado del Registro',
  `REGSTAMP` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Modificado'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='cliente vs condiciones de facturacion' ROW_FORMAT=DYNAMIC;

